import React from 'react'
import { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';

export default function Movie() {
  
  const [movie, setMovie] = useState()
  const [page, setPage] = useState(1)

  useEffect(() => {
    const options = {
      method: 'GET',
      headers: {
        accept: 'application/json',
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIyNGQwY2FkMGU1NDAyMzFkODQ1OTI1MjNhNzAxNzc1MyIsInN1YiI6IjY1YzA5NmVlYTM1YzhlMDE3Y2Q3ODE4YyIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.WsPfi3MDkQ3R01A5_w0Cn43_P8_8cCQp7cRoYyylPsg'
      }
    };
    
    fetch(`https://api.themoviedb.org/3/discover/movie?include_adult=false&include_video=false&language=ko-KR&page=${page}&sort_by=popularity.desc`, options)
      .then(response => response.json())
      .then(response => setMovie(response.results))
      .catch(err => console.error(err));
  }, [page]) // page가 변할때 마다 재랜더링

  return (
    <div className='flex-col w-4/5 bg-black'>
      <div className='text-center text-3xl font-appleB font-extrabold text-red-400'>Movie</div>

      <div className="flex flex-wrap justify-center items-center mb-5">
        {movie && movie.map((item, index) => (
          <div className="flex-col justify-center items-center">
            <img
              src={`https://image.tmdb.org/t/p/w500/${item.backdrop_path}`}
              alt={`Backdrop ${index}`}
              className="inline-block h-48 w-68 mb-2 m-2"
            />
            <div style={{ overflow: 'hidden', whiteSpace: 'nowrap', textOverflow: 'ellipsis', width: '272px', textAlign: 'center', color: "white" }}>{item.title}</div>
          </div>
        ))}
      </div>

      <div className='flex justify-center items-center'>
        <ul className="flex items-center -space-x-px h-8 text-sm">
          <li>
            <button onClick={() => setPage(page - 1)} disabled={page === 1} className="flex items-center justify-center px-3 h-8 ms-0 leading-tight text-gray-500 bg-white border border-e-0 border-gray-300 rounded-s-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white">
              Previous
            </button>
          </li>
          {Array.from({ length: 10 }, (_, i) => (
            <li key={i}>
              <button onClick={() => setPage(i + 1)} className={`flex items-center justify-center px-3 h-8 leading-tight text-gray-500 bg-white border border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white ${page === i + 1 ? 'bg-blue-50 text-blue-600' : ''}`}>
                {i + 1}
              </button>
            </li>
          ))}
          <li>
            <button onClick={() => setPage(page + 1)} disabled={page === 10} className="flex items-center justify-center px-3 h-8 leading-tight text-gray-500 bg-white border border-gray-300 rounded-e-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white">
              Next
            </button>
          </li>
        </ul>
      </div>
    </div>
  )
}
