import React from "react";

const MemberDeletePage = () => {

    const [email, setEmail] = useState("");
    const [confirmEmail, setConfirmEmail] = useState("");

    // 회원 계정을 삭제하는 함수
    const handleDelete = async () => {
        if (password !== confirmPassword) {
            return alert('Confirmed email is wrong')
        }

        fetch("/member/delete", {
                method: "DELETE",
                headers: { Authorization: localStorage.getItem("loginToken") }
            })
            .then(res => res.json())
            .then(data => {
                if (data.message === "탈퇴되었습니다") {
                    alert("정상적으로 탈퇴되었습니다")
                    window.location.href = "http://192.168.0.26:3000/" // 이거는 컴퓨터 ip주소로
                }
            })
            .catch((err) => console.error(err));
    };

    return (
        <div className="bg-black min-h-screen flex justify-center items-center">
            <div className="w-full max-w-md p-8 bg-white rounded-lg">
                <div className="text-2xl font-extrabold text-center text-red-400 mb-5">회원 계정 삭제</div>

                <form className="space-y-2 md:space-y-3">
                    <div>
                        <label htmlFor="email" className="block mb-2 text-base font-medium text-gray-900">Password</label>
                        <input type="text" name="password" id="password" onChange={(e) => setEmail(e.target.value)} className="bg-gray-50 border border-gray-300 text-black sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 " required="" />
                    </div>
                    <div>
                        <label htmlFor="confirm-email" className="block mb-2 text-base font-medium text-gray-900">Confirm password</label>
                        <input type="confirm-email" name="confirm-password" id="confirm-password" onChange={(e) => setConfirmEmail(e.target.value)} className="bg-gray-50 border border-gray-300 text-black sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 " required="" />
                    </div>
                    <button onClick={handleDelete}
                        className="w-full text-white bg-red-400 hover:bg-primary-700 focus:ring-4 focus:outline-none focus:ring-primary-300 font-semibold rounded-lg text-base px-5 py-2.5 text-center">Update</button>
                </form>
            </div>
        </div>


        // <div>
        //     <h2>회원 계정 삭제</h2>
        //     <p>정말로 계정을 삭제하시겠습니까?</p>
        //     <button onClick={handleDelete}>계정 삭제</button>
        // </div>
    );
};

export default MemberDeletePage;
