import React, { useEffect, useState } from 'react'

export default function LikeDislikeFunc({reviewId, love, hate}) {

    // 좋아요 갯수 담을 변수
    const [like, setLike] = useState(2)
    // 싫어요 갯수 담을 변수
    const [dislike, setDislike] = useState(2)
    // 좋아요 상태 변수 : 처음에는 좋아요 되어있지 않음
    const [isLiked, setIsLiked] = useState(false)
    // 싫어요 상태 변수 : 처음에는 싫어요 되어있지 않음
    const [isDisliked, setIsDisliked] = useState(false)


    const handleLike = () => {
        // 1. 아무것도 눌려져있지 않다면 좋아요 1 올리기
        if(!isLiked && !isDisliked) {
            setLike(like + 1)
            setIsLiked(true)
        } 
        // 2. 좋아요 버튼이 이미 클릭 되어있다면 좋아요 1 내리기
        else if(isLiked) {
            setLike(like - 1)
            setIsLiked(false)
        } 
        // 3. 싫어요 버튼이 이미 클릭 되어있다면 좋아요 1 올리고 싫어요 1 내리기
        else if(isDisliked) {
            setLike(like + 1)
            setIsLiked(true)
            setDislike(dislike - 1)
            setIsDisliked(false)
        }
    }

    const handleDislike = () => {
        // 1. 아무것도 클릭되어있지 않다면 싫어요 1 올리기
        if(!isLiked && !isDisliked) {
            setDislike(dislike + 1)
            setIsDisliked(true)
        }
        // 2. 싫어요 버튼이 이미 클릭되어있다면 싫어요 1 내리기
        else if(isDisliked) {
            setDislike(dislike - 1)
            setIsDisliked(false)
        }
        // 3. 좋아요 버튼이 이미 클릭 되어있다면 싫어요 1 올리기 좋아요 1 내리기
        else if(isLiked) {
            setDislike(dislike + 1)
            setIsDisliked(true)
            setLike(like - 1)
            setIsLiked(false)
        }
    }

    useEffect(() => {

        fetch(`http://192.168.56.1:8080/movie/love/${reviewId}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                love: isLiked ? 1 : -1  // 좋아요 상태에 따라 1 또는 -1 전달
            })
        })
            .then((res) => {
                // 응답 상태가 성공적이면 JSON으로 파싱
                if (!res.ok) {
                    throw new Error('Network response was not ok');
                }
                return res.json();
            })
            .then((data) => {
                console.log("서버 응답:", data); // 서버의 응답 내용 확인
            })
            .catch((err) => console.error(err));

    }, [like])

    useEffect(() => {

        fetch(`http://192.168.56.1:8080/movie/hate/${reviewId}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                hate: isDisliked ? 1 : -1  // 좋아요 상태에 따라 1 또는 -1 전달
            })
        })
            .then((res) => {
                // 응답 상태가 성공적이면 JSON으로 파싱
                if (!res.ok) {
                    throw new Error('Network response was not ok');
                }
                return res.json();
            })
            .then((data) => {
                console.log("서버 응답:", data); // 서버의 응답 내용 확인
            })
            .catch((err) => console.error(err));

    }, [dislike])

  return (
   <div>
        <button onClick={handleLike} className='text-white'>
            좋아요 {like}
        </button>

        <button onClick={handleDislike} className='text-white'>
            싫어요 {dislike}
        </button>
   </div>
  )
}
