import React, { useEffect, useState } from 'react'
import useMovieData from './UseMovieData';

export default function UpcomingList() {
    // const category = "upcoming"
    // const upcoming = useMovieData(category);

    const [upcoming, setUpcoming] = useState();

    useEffect(() => {
        const options = {
            method: 'GET',
            headers: {
              accept: 'application/json',
              Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIyNGQwY2FkMGU1NDAyMzFkODQ1OTI1MjNhNzAxNzc1MyIsInN1YiI6IjY1YzA5NmVlYTM1YzhlMDE3Y2Q3ODE4YyIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.WsPfi3MDkQ3R01A5_w0Cn43_P8_8cCQp7cRoYyylPsg'
            }
          };
          
          fetch('https://api.themoviedb.org/3/movie/upcoming?language=ko-KR&page=1', options)
            .then(response => response.json())
            .then(response => console.log(response))
            .catch(err => console.error(err));
    }, []);


    return (
        <div className='flex-col w-4/5 bg-black'>
            <div className='text-center text-3xl font-appleB font-extrabold text-red-400'>Upcoming</div>
            <div className='text-center text-3xl font-appleB font-extrabold text-red-400'>{upcoming && upcoming.dates.minimum} ~ {upcoming && upcoming.dates.maximum}</div>

            <div className="flex flex-wrap justify-center items-center">
                {upcoming && upcoming.results.map((item, index) => (
                    <div className="flex-col justify-center items-center">
                        <img
                            src={`https://image.tmdb.org/t/p/w500/${item.backdrop_path}`}
                            alt={`Backdrop ${index}`}
                            className="inline-block h-48 w-68 mb-2 m-2"
                        />
                        <div style={{ overflow: 'hidden', whiteSpace: 'nowrap', textOverflow: 'ellipsis', width: '272px', textAlign: 'center', color: "white" }}>{item.title}</div>
                    </div>
                ))}
            </div>
        </div>
    )
}
