import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import FindMovieData from "./FindMovieData";
import StarIcon from "@mui/icons-material/Star";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import { red } from "@mui/material/colors";

import { Bounce, ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function MovieInfo_renew() {

  const param = useParams().index;
  console.log("Movie id : ", param);

  const id = param;
  const movieInfo = FindMovieData(id);

  console.log("Movie info : ", movieInfo);

  const [dbReview, setDbReview] = useState([]);
  const [content, setContent] = useState('');
  const [rating, setRating] = useState('');
  const [selectedReviewId, setSelectedReviewId] = useState(null);
  const [selectedDeleteId, setSelectedDeleteId] = useState(null);
  const [selectedContent, setSelectedContent] = useState('');
  const [selectedRating, setSelectedRating] = useState('');
  const [sortBy, setSortBy] = useState("date"); // 정렬 기준
  const [orderBy, setOrderBy] = useState("desc"); // 정렬 방식
  const [sortedReviews, setSortedReviews] = useState([]);

  // 리뷰 DB에서 리뷰 정보 받아오기
  const getDbReview = (id) => {
    fetch(`http://192.168.56.1:8080/movie/review/${id}`, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("리뷰 데이터: ", data);
        setDbReview(data);
        setSortedReviews(data); // 초기 상태 저장
      })
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    getDbReview(id); // 초기 데이터 로드
  }, [id]);

  function handleSortReviews() {
    let newSortedReviews = [...sortedReviews];  // dbReview에서 가져온 원본 리스트를 복사
  
    if (sortBy === 'rating') {
      // 평점 순으로 정렬
      newSortedReviews.sort((a, b) => {
        return orderBy === 'asc' ? a.rating - b.rating : b.rating - a.rating;
      });
    } else if (sortBy === 'date') {
      // 날짜 순으로 정렬
      newSortedReviews.sort((a, b) => {
        return orderBy === 'asc' ? new Date(a.date) - new Date(b.date) : new Date(b.date) - new Date(a.date);
      });
    }
  
    setSortedReviews(newSortedReviews);  // 정렬된 리스트 저장
  }
  
  
  

  useEffect(() => {
    handleSortReviews(sortBy, orderBy);
  }, [sortBy, orderBy]);

  // 작성자 이메일을 받아와서 이름 분리
  const getWriterId = (email) => {
    return email ? email.substring(0, email.indexOf("@")) : "Unknown";
  };
  

  // 리뷰 내용 받아오기
  const handleContent = (e) => {
    setContent(e.target.value);
  };

  // 리뷰 별점 받아오기
  const handleRating = (e) => {
    setRating(e.target.value);
  };

  // toast 설정
  const toastSuccess = (message) => {
    toast.success(message);
  };

  const toastErr = (message) => {
    toast.error(message);
  };

  // 리뷰 등록 fetch
  const handleInsert = () => {
    fetch(`http://192.168.56.1:8080/movie/review/${id}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
      body: JSON.stringify({
        content: content,
        grade: rating,
      }),
    })
      .then((res) => {
        if (res.ok) {
          toastSuccess("리뷰 등록 성공");
          getDbReview(id); // 데이터 새로고침
        } else {
          throw new Error("리뷰 등록 실패");
        }
      })
      .catch((err) => {
        console.error(err);
        toastErr('리뷰 등록 권한 에러');
      });
  };

  // 리뷰 수정 fetch
  const handleUpdate = (reviewId) => {
    fetch(`http://192.168.56.1:8080/movie/review/${reviewId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
      body: JSON.stringify({
        content: content,
        grade: rating,
      })
    })
      .then((res) => {
        if (res.ok) {
          toastSuccess("리뷰 수정 성공");
          getDbReview(id); // 데이터 새로고침
        } else {
          throw new Error("리뷰 수정 실패");
        }
      })
      .catch((err) => {
        console.error(err);
        toastErr('리뷰 수정 권한 에러');
      });
  };

  // 리뷰 삭제 fetch
  const handleDelete = (reviewId) => {
    fetch(`http://192.168.56.1:8080/movie/review/${reviewId}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("Error");
        }
        toastSuccess("리뷰 삭제 성공");
        getDbReview(id); // 데이터 새로고침
      })
      .catch((err) => {
        console.error(err);
        toastErr('리뷰 삭제 권한 에러');
      });
  };

  const [openModify, setOpenModify] = React.useState(false);
  const [openDelete, setOpenDelete] = React.useState(false);
  const [openInsert, setOpenInsert] = React.useState(false);

  const handleClickOpenUpdate = (e, reviewId, content, rating) => {
    setSelectedReviewId(reviewId);
    setSelectedContent(content);
    setSelectedRating(rating);
    setOpenModify(true);
  };

  const handleClickOpenDelete = (e, reviewId) => {
    setSelectedDeleteId(reviewId);
    setOpenDelete(true);
  };

  const handleClickOpenInsert = (e) => {
    setOpenInsert(true);
  };

  const handleClose = () => {
    setSelectedReviewId(null);
    setSelectedDeleteId(null);
    setOpenModify(false);
    setOpenDelete(false);
    setOpenInsert(false);
  };

  return (
    <div className="bg-black h-full w-full">
      {movieInfo && (
        <div>
          <div className="p-10 flex">
            <img
              src={`https://image.tmdb.org/t/p/w500/${movieInfo.backdrop_path}`}
              className="w-full mb-10"
            />
            <div className="ml-5">
              <div className="text-3xl font-extrabold mb-3 text-white">
                {movieInfo.title}
              </div>
              <div className="text-2xl font-extrabold mb-3 text-white">
                {movieInfo.original_title}
              </div>
              <div className="text-xl font-extrabold mb-3 text-white">
                {movieInfo.tagline}
              </div>
              <div className="text-lg font-medium text-white">
                개봉일: {movieInfo.release_date}
              </div>
              <div className="text-lg font-medium text-white">
                상영시간: {movieInfo.runtime}분
              </div>
              <div className="text-lg font-medium text-white">
                부적절 등급: {movieInfo.adult ? "청소년 관람 불가" : "청소년 관람 가능"}
              </div>
              <div className="text-lg font-medium text-white">
                장르: {movieInfo.genres && movieInfo.genres.map(genre => genre.name).join(', ')}
              </div>
              <div className="flex justify-start items-start text-lg font-medium text-white">
                <span className="inline-block w-auto lg:min-w-20">시놉시스:</span>
                <div className="ml-1 w-full">{movieInfo.overview}</div>
              </div>
              <div className="text-lg font-medium text-white">
                관람객: {movieInfo.popularity}명
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="flex justify-end items-center text-white mb-3 mr-12">
        {/* 리뷰 등록 모달 */}
        <React.Fragment>
          <Button variant="outlined" onClick={handleClickOpenInsert}
            style={{
              backgroundColor: red[400],
              fontWeight: "bold",
              color: "white",
              borderColor: red[400],
              marginRight: "5px",
            }}
          >
            리뷰 등록
          </Button>

          <Dialog open={openInsert} onClose={handleClose}>
            <DialogTitle>리뷰 등록</DialogTitle>
            <DialogContent>
              <DialogContentText>
                영화에 대한 리뷰를 작성해주세요.
              </DialogContentText>
              <TextField
                autoFocus
                margin="dense"
                id="content"
                label="리뷰 내용"
                type="text"
                fullWidth
                variant="standard"
                value={content}
                onChange={handleContent}
              />
              <TextField
                margin="dense"
                id="rating"
                label="평점"
                type="number"
                fullWidth
                variant="standard"
                value={rating}
                onChange={handleRating}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose}>취소</Button>
              <Button onClick={handleInsert}>등록</Button>
            </DialogActions>
          </Dialog>

          {/* 리뷰 수정 모달 */}
          <Dialog open={openModify} onClose={handleClose}>
            <DialogTitle>리뷰 수정</DialogTitle>
            <DialogContent>
              <DialogContentText>
                영화에 대한 리뷰를 수정해주세요.
              </DialogContentText>
              <TextField
                autoFocus
                margin="dense"
                id="content"
                label="리뷰 내용"
                type="text"
                fullWidth
                variant="standard"
                value={selectedContent}
                onChange={(e) => setSelectedContent(e.target.value)}
              />
              <TextField
                margin="dense"
                id="rating"
                label="평점"
                type="number"
                fullWidth
                variant="standard"
                value={selectedRating}
                onChange={(e) => setSelectedRating(e.target.value)}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose}>취소</Button>
              <Button
                onClick={() => {
                  handleUpdate(selectedReviewId);
                  handleClose();
                }}
              >
                수정
              </Button>
            </DialogActions>
          </Dialog>

          {/* 리뷰 삭제 모달 */}
          <Dialog open={openDelete} onClose={handleClose}>
            <DialogTitle>리뷰 삭제</DialogTitle>
            <DialogContent>
              <DialogContentText>
                리뷰를 삭제하시겠습니까?
              </DialogContentText>
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose}>취소</Button>
              <Button
                onClick={() => {
                  handleDelete(selectedDeleteId);
                  handleClose();
                }}
              >
                삭제
              </Button>
            </DialogActions>
          </Dialog>
        </React.Fragment>
      </div>

      {/* 정렬 기준 및 방식 선택 */}
      <div className="flex justify-end items-center mb-3 mr-12 text-white">
        <div className="flex items-center">
          <span className="mr-2">정렬 기준:</span>
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="mr-4 p-1 bg-gray-800 border border-gray-700 rounded"
          >
            <option value="date">날짜</option>
            <option value="rating">평점</option>
          </select>
          <span className="mr-2">정렬 방식:</span>
          <select
            value={orderBy}
            onChange={(e) => setOrderBy(e.target.value)}
            className="p-1 bg-gray-800 border border-gray-700 rounded"
          >
            <option value="desc">내림차순</option>
            <option value="asc">오름차순</option>
          </select>
        </div>
      </div>

      <div className="px-10">
        {dbReview && dbReview.map((review) => (
          <div key={review.id} className="border-b border-gray-700 py-2">
            <div className="flex items-center mb-2">
              <div className="mr-2 text-lg text-white font-bold">
                {getWriterId(review.email)}
              </div>
              <StarIcon color="warning" />
              <div className="ml-1 text-lg text-white">{review.grade}</div>
            </div>
            <div className="text-white mb-2">{review.content}</div>
            <div className="text-gray-500 text-sm">
              {new Date(review.date).toLocaleDateString()}
            </div>
            <div className="flex mt-2">
              <Button
                onClick={(e) => handleClickOpenUpdate(e, review.id, review.content, review.grade)}
                className="mr-2"
                variant="contained"
                color="primary"
              >
                수정
              </Button>
              <Button
                onClick={(e) => handleClickOpenDelete(e, review.id)}
                variant="contained"
                color="secondary"
              >
                삭제
              </Button>
            </div>
          </div>
        ))}
      </div>

      <ToastContainer
        position="bottom-center"
        autoClose={2000}
        hideProgressBar
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="dark"
      />
    </div>
  );
}
