import React, { useEffect, useState } from 'react';

export default function LikeDislikeFunc({ reviewId, love, hate }) {
  const [like, setLike] = useState(love);
  const [dislike, setDislike] = useState(hate);
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);

  const [isProcessing, setIsProcessing] = useState(false);
  useEffect(() => {
    if (isLiked !== null) {
        fetch(`http://192.168.56.1:8080/movie/love/${reviewId}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                love: isLiked ? 1 : -1,
            }),
        })
            .then((res) => {
                if (!res.ok) {
                    throw new Error('Network response was not ok');
                }
                return res.json();
            })
            .then((data) => {
                console.log('서버 응답:', data);
                // 서버 응답의 값과 동기화
                setLike(data.love);
                setDislike(data.hate);
            })
            .catch((err) => console.error(err));
    }
}, [isLiked]);

useEffect(() => {
    if (isDisliked !== null) {
        fetch(`http://192.168.56.1:8080/movie/hate/${reviewId}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                hate: isDisliked ? 1 : -1,
            }),
        })
            .then((res) => {
                if (!res.ok) {
                    throw new Error('Network response was not ok');
                }
                return res.json();
            })
            .then((data) => {
                console.log('서버 응답:', data);
                // 서버 응답의 값과 동기화
                setDislike(data.hate);
                setLike(data.love);
            })
            .catch((err) => console.error(err));
    }
}, [isDisliked, reviewId]);

