const handleLike = () => {
    if (isProcessing) return; // 이미 처리 중이라면 리턴

    setIsProcessing(true); // 처리 시작
    // 1. 아무것도 눌려져있지 않다면 좋아요 1 올리기
    if (!isLiked && !isDisliked) {
        setLike((prev) => prev + 1);
        setIsLiked(true);
    }
    // 2. 좋아요 버튼이 이미 클릭 되어있다면 좋아요 1 내리기
    else if (isLiked) {
        setLike((prev) => prev - 1);
        setIsLiked(false);
    }
    // 3. 싫어요 버튼이 이미 클릭 되어있다면 좋아요 1 올리고 싫어요 1 내리기
    else if (isDisliked) {
        setLike((prev) => prev + 1);
        setIsLiked(true);
        setDislike((prev) => {
            const newDislikeCount = prev - 1;
            return newDislikeCount < 0 ? 0 : newDislikeCount; // 0 미만이 되지 않도록 설정
        });
        setIsDisliked(false);
    }
};

const handleDislike = () => {
    if (isProcessing) return; // 이미 처리 중이라면 리턴

    setIsProcessing(true); // 처리 시작
    // 1. 아무것도 클릭되어있지 않다면 싫어요 1 올리기
    if (!isLiked && !isDisliked) {
        setDislike((prev) => prev + 1);
        setIsDisliked(true);
    }
    // 2. 싫어요 버튼이 이미 클릭되어있다면 싫어요 1 내리기
    else if (isDisliked) {
        setDislike((prev) => {
            const newDislikeCount = prev - 1;
            return newDislikeCount < 0 ? 0 : newDislikeCount; // 0 미만이 되지 않도록 설정
        });
        setIsDisliked(false);
    }
    // 3. 좋아요 버튼이 이미 클릭 되어있다면 싫어요 1 올리고 좋아요 1 내리기
    else if (isLiked) {
        setDislike((prev) => prev + 1);
        setIsDisliked(true);
        setLike((prev) => {
            const newLikeCount = prev - 1;
            return newLikeCount < 0 ? 0 : newLikeCount; // 0 미만이 되지 않도록 설정
        });
        setIsLiked(false);
    }
};

  return (
    <div>
      <button onClick={handleLike} className="text-white">
        좋아요 {like}
      </button>

      <button onClick={handleDislike} className="text-white">
        싫어요 {dislike}
      </button>
    </div>
  );
}
