import React, { useState } from 'react';

function LikeDislikeFunc({reviewId}) {
    const [like, setLike] = useState(0);
    const [dislike, setDislike] = useState(0);
    const [isLiked, setIsLiked] = useState(false);
    const [isDisliked, setIsDisliked] = useState(false);

    const updateLikeDislike = (type) => {
        const loveValue = type === 'like' ? 1 : -1; // 'like'Ïùº Í≤ΩÏö∞ 1, 'dislike'Ïùº Í≤ΩÏö∞ -1
        fetch(`http://192.168.56.1:8080/movie/${reviewId}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                love: loveValue
            })
        })
            .then((res) => res.json())
            .then((data) => { console.log("Ï¢ãÏïÑÏöî Ïã´Ïñ¥Ïöî: ", data) })
            .catch((err) => console.error(err));
    }


    // Ï¢ãÏïÑÏöî Î≤ÑÌäº ÌÅ¥Î¶≠ Ï≤òÎ¶¨ Ìï®Ïàò
    const handleLike = () => {
        if (isLiked) {
            setLike(like - 1); // Ï¢ãÏïÑÏöî Ï∑®ÏÜå
            setIsLiked(false);
            updateLikeDislike(like) // Ï¢ãÏïÑÏöî Ï∑®ÏÜå Ï†ÑÏÜ°
        } else {
            setLike(like + 1); // Ï¢ãÏïÑÏöî
            setIsLiked(true);
            updateLikeDislike(like) // Ï¢ãÏïÑÏöî Ï†ÑÏÜ°
            if (isDisliked) {
                setDislike(dislike - 1); // Ïã´Ïñ¥Ïöî Ï∑®ÏÜå
                setIsDisliked(false);
                updateLikeDislike(dislike) // Ïã´Ïñ¥Ïöî Ï∑®ÏÜå Ï†ÑÏÜ°
            }
        }
    };

    // Ïã´Ïñ¥Ïöî Î≤ÑÌäº ÌÅ¥Î¶≠ Ï≤òÎ¶¨ Ìï®Ïàò
    const handleDislike = () => {
        if (isDisliked) {
            setDislike(dislike - 1); // Ïã´Ïñ¥Ïöî Ï∑®ÏÜå
            setIsDisliked(false);
            updateLikeDislike(dislike) // Ïã´Ïñ¥Ïöî Ï∑®ÏÜå Ï†ÑÏÜ°
        } else {
            setDislike(dislike + 1); // Ïã´Ïñ¥Ïöî
            setIsDisliked(true);
            updateLikeDislike(dislike) // Ïã´Ïñ¥Ïöî Ï†ÑÏÜ°
            if (isLiked) {
                setLike(like - 1); // Ï¢ãÏïÑÏöî Ï∑®ÏÜå
                setIsLiked(false);
                updateLikeDislike(like) // Ï¢ãÏïÑÏöî Ï∑®ÏÜå Ï†ÑÏÜ°
            }
        }
    };

    return (
        <div>
            <button onClick={handleLike} className='text-white'>
                üëç Ï¢ãÏïÑÏöî {like}
            </button>
            <button onClick={handleDislike} className='text-white'>
                üëé Ïã´Ïñ¥Ïöî {dislike}
            </button>
        </div>
    );
}

export default LikeDislikeFunc;
