import React, { useState } from "react";

const MemberUpdatePage = () => {
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    // 회원 정보를 업데이트하는 함수
    const handleUpdate = async () => {
        if (password !== confirmPassword) {
            return alert('Confirmed password is wrong')
        }

        try {
            const response = await fetch("/member/update", {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ password }), // JSON 형태로 데이터 전송
            });
            const data = await response.json();
            console.log(data); // 성공 메시지 처리
        } catch (error) {
            console.error("회원 정보 업데이트 에러:", error);
        }
    };

    return (
        <div className="bg-black h-screen flex justify-center items-center">
            <div className="flex flex-col justify-start items-center bg-white p-8 rounded-lg">
                <div className="text-2xl font-extrabold text-center text-red-400">회원 정보 수정</div>
                {/* <div>
                        <label>이름:</label>
                        <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        />
                    </div> */}

                <form className="space-y-2 md:space-y-3 w-">
                    <div>
                        <label htmlFor="password" className="block mb-2 text-base font-medium text-gray-900">Password</label>
                        <input type="text" name="password" id="password" onChange={(e) => setPassword(e.target.value)} className="bg-gray-50 border border-gray-300 text-black sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 " required="" />
                    </div>
                    <div>
                        <label htmlFor="confirm-password" className="block mb-2 text-base font-medium text-gray-900">Confirm password</label>
                        <input type="confirm-password" name="confirm-password" id="confirm-password" onChange={(e) => setConfirmPassword(e.target.value)} className="bg-gray-50 border border-gray-300 text-black sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 " required="" />
                    </div>
                    <button onClick={handleUpdate}
                        className="w-full text-white bg-red-400 hover:bg-primary-700 focus:ring-4 focus:outline-none focus:ring-primary-300 font-semibold rounded-lg text-base px-5 py-2.5 text-center">Update</button>
                </form>

                {/* <div className="text-center text-white">
                        <label>Password: </label>
                        <input
                        type="text"
                        onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <div className="text-center text-white">
                        <label>Confirm Password: </label>
                        <input
                        type="text"
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        />
                    </div>
                    <button onClick={handleUpdate}>수정</button>
                    </div> */}
            </div>
        </div>
    );
};

export default MemberUpdatePage;
