import React from "react";

const MemberDeletePage = () => {
    // 회원 계정을 삭제하는 함수
    const handleDelete = async () => {
        try {
            const response = await fetch("/member/delete", {
                method: "DELETE",
                headers: { Authorization: localStorage.getItem("loginToken") }
            });
            const data = await response.json();
            console.log(data); // 성공 메시지 처리
        } catch (error) {
            console.error("회원 계정 삭제 에러:", error);
        } fetch("http://10.125.121.181:8080/member/delete", {
            method: "DELETE",
            headers: { Authorization: localStorage.getItem("loginToken") }
        })
            .then(res => res.json())
            .then(data => {
                if (data.message === "탈퇴되었습니다") {
                    alert("정상적으로 탈퇴되었씁니다")
                    window.location.href = "http://192.168.0.26:3000/" // 이거는 컴퓨터 ip주소로
                }
            })
            .catch((err) => console.error(err));
    };

    return (
        <div>
            <h2>회원 계정 삭제</h2>
            <p>정말로 계정을 삭제하시겠습니까?</p>
            <button onClick={handleDelete}>계정 삭제</button>
        </div>
    );
};

export default MemberDeletePage;
