import React, { useState, useEffect } from 'react';

function LikeDislikeFunc({ reviewId }) {
    const [like, setLike] = useState(0);
    const [dislike, setDislike] = useState(0);
    const [isLiked, setIsLiked] = useState(false);
    const [isDisliked, setIsDisliked] = useState(false);
    const [loading, setLoading] = useState(false); // 로딩 상태 추가

    useEffect(() => {
        // 컴포넌트가 마운트될 때 좋아요 및 싫어요 수 가져오기
        const fetchLikesAndDislikes = async () => {
            setLoading(true);
            try {
                const response = await fetch(`http://192.168.56.1:8080/movie/review/${reviewId}`);
                if (!response.ok) throw new Error('Network response was not ok');
                const data = await response.json();
                setLike(data.love);
                setDislike(data.hate);
                // 사용자 상태를 설정
                setIsLiked(data.userLiked); // 서버에서 사용자 좋아요 상태를 받아옴
                setIsDisliked(data.userDisliked); // 서버에서 사용자 싫어요 상태를 받아옴
            } catch (error) {
                console.error("Error fetching likes and dislikes:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchLikesAndDislikes();
    }, [reviewId]);

    const updateLike = async (value) => {
        setLoading(true);
        try {
            const response = await fetch(`http://192.168.56.1:8080/movie/love/${reviewId}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ love: value })
            });
            if (!response.ok) throw new Error('Network response was not ok');
            const data = await response.json();
            setLike(data.love); // 서버 응답에 따른 업데이트
            setIsLiked(value === 1);
            setIsDisliked(false); // 좋아요를 누르면 싫어요는 자동으로 취소
        } catch (error) {
            console.error(error);
        } finally {
            setLoading(false); // 로딩 종료
        }
    };

    const updateDislike = async (value) => {
        setLoading(true);
        try {
            const response = await fetch(`http://192.168.56.1:8080/movie/hate/${reviewId}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ hate: value })
            });
            if (!response.ok) throw new Error('Network response was not ok');
            const data = await response.json();
            setDislike(data.hate); // 서버 응답에 따른 업데이트
            setIsDisliked(value === 1);
            setIsLiked(false); // 싫어요를 누르면 좋아요는 자동으로 취소
        } catch (error) {
            console.error(error);
        } finally {
            setLoading(false); // 로딩 종료
        }
    };

    const handleLike = () => {
        if (isLiked) {
            updateLike(0); // 좋아요 취소 전송
        } else {
            updateLike(1); // 좋아요 전송
            if (isDisliked) {
                updateDislike(0); // 싫어요 취소 전송
            }
        }
    };

    const handleDislike = () => {
        if (isDisliked) {
            updateDislike(0); // 싫어요 취소 전송
        } else {
            updateDislike(1); // 싫어요 전송
            if (isLiked) {
                updateLike(0); // 좋아요 취소 전송
            }
        }
    };

    return (
        <div>
            <button onClick={handleLike} disabled={loading} className='text-white mr-2'>
                👍 좋아요 {like}
            </button>
            <button onClick={handleDislike} disabled={loading} className='text-white'>
                👎 싫어요 {dislike}
            </button>
        </div>
    );
}

export default LikeDislikeFunc;
