import React, { useState, useEffect } from 'react';

function LikeDislikeFunc({ reviewId }) {
    const [like, setLike] = useState(0);
    const [dislike, setDislike] = useState(0);
    const [isLiked, setIsLiked] = useState(false);
    const [isDisliked, setIsDisliked] = useState(false);
    const [loading, setLoading] = useState(false); // Î°úÎî© ÏÉÅÌÉú Ï∂îÍ∞Ä

    useEffect(() => {
        // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä ÎßàÏö¥Ìä∏Îê† Îïå Ï¢ãÏïÑÏöî Î∞è Ïã´Ïñ¥Ïöî Ïàò Í∞ÄÏ†∏Ïò§Í∏∞
        const fetchLikesAndDislikes = async () => {
            setLoading(true);
            try {
                const response = await fetch(`http://192.168.56.1:8080/movie/review/${reviewId}`);
                if (!response.ok) throw new Error('Network response was not ok');
                const data = await response.json();
                setLike(data.love);
                setDislike(data.hate);
                // ÏÇ¨Ïö©Ïûê ÏÉÅÌÉúÎ•º ÏÑ§Ï†ï
                setIsLiked(data.userLiked); // ÏÑúÎ≤ÑÏóêÏÑú ÏÇ¨Ïö©Ïûê Ï¢ãÏïÑÏöî ÏÉÅÌÉúÎ•º Î∞õÏïÑÏò¥
                setIsDisliked(data.userDisliked); // ÏÑúÎ≤ÑÏóêÏÑú ÏÇ¨Ïö©Ïûê Ïã´Ïñ¥Ïöî ÏÉÅÌÉúÎ•º Î∞õÏïÑÏò¥
            } catch (error) {
                console.error("Error fetching likes and dislikes:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchLikesAndDislikes();
    }, [reviewId]);

    const updateLike = async (value) => {
        setLoading(true);
        try {
            const response = await fetch(`http://192.168.56.1:8080/movie/love/${reviewId}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ love: value })
            });
            if (!response.ok) throw new Error('Network response was not ok');
            const data = await response.json();
            setLike(data.love); // ÏÑúÎ≤Ñ ÏùëÎãµÏóê Îî∞Î•∏ ÏóÖÎç∞Ïù¥Ìä∏
            setIsLiked(value === 1);
            setIsDisliked(false); // Ï¢ãÏïÑÏöîÎ•º ÎàÑÎ•¥Î©¥ Ïã´Ïñ¥ÏöîÎäî ÏûêÎèôÏúºÎ°ú Ï∑®ÏÜå
        } catch (error) {
            console.error(error);
        } finally {
            setLoading(false); // Î°úÎî© Ï¢ÖÎ£å
        }
    };

    const updateDislike = async (value) => {
        setLoading(true);
        try {
            const response = await fetch(`http://192.168.56.1:8080/movie/hate/${reviewId}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ hate: value })
            });
            if (!response.ok) throw new Error('Network response was not ok');
            const data = await response.json();
            setDislike(data.hate); // ÏÑúÎ≤Ñ ÏùëÎãµÏóê Îî∞Î•∏ ÏóÖÎç∞Ïù¥Ìä∏
            setIsDisliked(value === 1);
            setIsLiked(false); // Ïã´Ïñ¥ÏöîÎ•º ÎàÑÎ•¥Î©¥ Ï¢ãÏïÑÏöîÎäî ÏûêÎèôÏúºÎ°ú Ï∑®ÏÜå
        } catch (error) {
            console.error(error);
        } finally {
            setLoading(false); // Î°úÎî© Ï¢ÖÎ£å
        }
    };

    const handleLike = () => {
        if (isLiked) {
            updateLike(0); // Ï¢ãÏïÑÏöî Ï∑®ÏÜå Ï†ÑÏÜ°
        } else {
            updateLike(1); // Ï¢ãÏïÑÏöî Ï†ÑÏÜ°
            if (isDisliked) {
                updateDislike(0); // Ïã´Ïñ¥Ïöî Ï∑®ÏÜå Ï†ÑÏÜ°
            }
        }
    };

    const handleDislike = () => {
        if (isDisliked) {
            updateDislike(0); // Ïã´Ïñ¥Ïöî Ï∑®ÏÜå Ï†ÑÏÜ°
        } else {
            updateDislike(1); // Ïã´Ïñ¥Ïöî Ï†ÑÏÜ°
            if (isLiked) {
                updateLike(0); // Ï¢ãÏïÑÏöî Ï∑®ÏÜå Ï†ÑÏÜ°
            }
        }
    };

    return (
        <div>
            <button onClick={handleLike} disabled={loading} className='text-white mr-2'>
                üëç Ï¢ãÏïÑÏöî {like}
            </button>
            <button onClick={handleDislike} disabled={loading} className='text-white'>
                üëé Ïã´Ïñ¥Ïöî {dislike}
            </button>
        </div>
    );
}

export default LikeDislikeFunc;
