import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { userEmail } from "./AtomSt";
import { useRecoilState } from "recoil";

import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import StarIcon from "@mui/icons-material/Star";
import { red } from "@mui/material/colors";

import { Bounce, ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import FindMovieData from "./FindMovieData";

export default function MovieInfo() {

  const param = useParams().index;
  console.log("Movie id : ", param)

  const id = param
  const movieInfo = FindMovieData(id);

  console.log("Movie info : ", movieInfo)

  useEffect(() => {
    getMovie(param);

    const id = parseInt(param) + 1
    getDbMovie(id)
    getDbReview(id, sortBy, orderBy); // sortBy와 orderBy 값을 전달
  }, [param, sortBy, orderBy]);

  const [openModify, setOpenModify] = React.useState(false);
  const [openDelete, setOpenDelete] = React.useState(false);
  const [openInsert, setOpenInsert] = React.useState(false);

  const handleClickOpenUpdate = (e, reviewId, content, grade) => {
    setSelectedReviewId(reviewId);
    setSelectedContent(content);
    setselectedGrade(grade);
    setOpenModify(true);
  };

  const handleClickOpenDelete = (e, reviewId) => {
    setSelectedDeleteId(reviewId);
    setOpenDelete(true);
  };

  const handleClickOpenInsert = (e) => {
    setOpenInsert(true);
  };

  const handleClose = () => {
    setSelectedReviewId(null);
    setSelectedDeleteId(null);
    setOpenModify(false);
    setOpenDelete(false);
    setOpenInsert(false);
  };

  const divWriter = document.getElementById("writer");

  const getWriterId = (string) => {
    return string.substring(0, string.indexOf("@"));
  };

  const handleContent = (e) => {
    setContent(e.target.value);
  };

  const handleGrade = (e) => {
    setGrade(e.target.value);
  };

  // 리뷰 수정 fetch
  const handleUpdate = (reviewId) => {
    fetch(`http://192.168.56.1:8080/movie/review/${reviewId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
      body: JSON.stringify({
        content: content,
        grade: grade,
      })
    })
      .then((res) => {
        if (res.ok) {
          toastSuccess("리뷰 수정 성공")
          window.location.reload()
        } else {
          throw new Error("리뷰 수정 실패")
        }
      })
      .catch((err) => {
        console.error(err)
        toastErr('리뷰 수정 권한 에러');
      })
  };

  // 리뷰 삭제 fetch
  const handleDelete = (reviewId) => {
    fetch(`http://192.168.56.1:8080/movie/review/${reviewId}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("Error");
        }
        toastSuccess("리뷰 삭제 성공");
        window.location.reload();
      })
      .catch((err) => {
        console.error(err)
        toastErr('리뷰 삭제 권한 에러')
      });
  };

  // 리뷰 등록 fetch
  const handleInsert = () => {
    fetch(`http://192.168.56.1:8080/movie/review/${dbData.movie_id}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
      body: JSON.stringify({
        content: content,
        grade: grade,
      }),
    })
      .then((res) => {
        if (res.ok) {
          toastSuccess("리뷰 등록 성공")
          window.location.reload()
        } else {
          throw new Error("리뷰 등록 실패")
        }
      })
      .catch((err) => {
        console.error(err)
        toastErr('리뷰 등록 권한 에러')
      });
  };

  const toastSuccess = (message) => {
    toast.success(message)
  }

  const toastErr = (message) => {
    toast.error(message)
  }

  return (
    <div className="bg-black h-full w-full">
      {movieInfo && (
        <div>
          <div className="p-10 flex-col">
            <img
              src={`https://image.tmdb.org/t/p/w500/${movieInfo.backdrop_path}`}
              className="w-full mb-10"
            />
            {/* <img src={posterImage[param]} alt="Movie Poster" className="w-72 mb-5" /> */}
            <div className="ml-5">
              <div className="text-3xl font-extrabold mb-3 text-white">
                {movieInfo.title}
              </div>
              <div className="text-2xl font-extrabold mb-3 text-white">
                {movieInfo.original_title}
              </div>
              <div className="text-xl font-extrabold mb-3 text-white">
                {movieInfo.tagline}
              </div>
              <div className="text-lg font-medium text-white">
                개봉일: {movieInfo.release_date}
              </div>
              <div className="text-lg font-medium text-white">
                상영시간: {movieInfo.runtime}분
              </div>
              <div className="text-lg font-medium text-white">
                부적절 등급: {movieInfo.adult ? "청소년 관람 불가" : "청소년 관람 가능"}
              </div>
              <div className="text-lg font-medium text-white">
                장르: {movieInfo.genres && movieInfo.genres.map(genre => genre.name).join(', ')}
              </div>
              {/* <div className="text-lg font-medium text-white">
                시리즈: {movieInfo.belongs_to_collection.name}
              </div> */}
              <div className="text-lg font-medium text-white">
                시놉시스: {movieInfo.overview}
              </div>
              {/* <div className="text-lg font-medium text-white">
                제공사: {movieInfo.production_companies[0].name}
              </div> */}
              <div className="text-lg font-medium text-white">
                관람객: {movieInfo.popularity}명
              </div>
            </div>
          </div>
          <div className="flex justify-end items-center text-white mb-3 mr-12">
            리뷰 등록 모달
            <React.Fragment>
              <Button variant="outlined" onClick={(e) => handleClickOpenInsert(e)}
                style={{
                  backgroundColor: red[400], // 300하면 비슷한데 음 몰라
                  fontWeight: "bold",
                  color: "white",
                  borderColor: red[400],
                  marginRight: "5px",
                }}>
                등록
              </Button>
              <Dialog open={openInsert} onClose={handleClose}>
                <DialogTitle>등록</DialogTitle>
                <DialogContent>
                  <DialogContentText>
                    등록할 내용을 작성해주세요
                  </DialogContentText>
                  <TextField
                    disabled
                    id="email"
                    label="Email"
                    defaultValue={localStorage.getItem("userId")}
                    variant="standard"
                    fullWidth
                  />
                  <TextField
                    onChange={handleContent}
                    autoFocus
                    required
                    margin="dense"
                    id="content"
                    name="content"
                    label="새로운 내용"
                    type="text"
                    fullWidth
                    variant="standard"
                  />
                  <TextField
                    onChange={handleGrade}
                    autoFocus
                    required
                    margin="dense"
                    id="grade"
                    name="grade"
                    label="새로운 등급"
                    type="number"
                    fullWidth
                    variant="standard"
                  />
                </DialogContent>
                <DialogActions>
                  <Button onClick={handleClose}>취소</Button>
                  <Button onClick={handleInsert}>등록</Button>
                </DialogActions>
              </Dialog>
            </React.Fragment>
          </div>
          {/* <div className="flex justify-end m-3">
            리뷰 정렬 버튼
            <button
              variant="outlined"
              className="hover:underline text-white px-4 py-2 rounded-md mr-2"
              onClick={() => {
                // setSortBy(sortBy === "grade" ? "date" : "grade")
                setSortBy("grade")
                setOrderBy(orderBy === "asc" ? "desc" : "asc");
                // handleSortReviews(orderBy === "asc" ? "desc" : "asc");
              }}>
              {orderBy === "asc" ? "▼ 등급 낮은 순" : "▲ 등급 높은 순"}
            </button>
            <button
              variant="outlined"
              className="hover:underline text-white px-4 py-2 rounded-md mr-5"
              onClick={() => {
                // setSortBy(sortBy === "date" ? "grade" : "date")
                setSortBy("date")
                setOrderBy(orderBy === "asc" ? "desc" : "asc");
              }}>
              {orderBy === "asc" ? "▼ 오래된 순" : "▲ 최신 순"}
            </button>
          </div> */}
          {/* <div className="pl-10 pr-10 pb-10">
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
              {dbReview.map((rv, idx) => (
                <div key={idx} className="bg-gray-800 rounded-lg overflow-hidden">
                  <div className="p-4">
                    <div className="flex items-center mb-2">
                      <svg className="fill-white h-10 w-10 mr-3" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
                        <path d="M224 256A128 128 0 1 0 224 0a128 128 0 1 0 0 256zm-45.7 48C79.8 304 0 383.8 0 482.3C0 498.7 13.3 512 29.7 512H418.3c16.4 0 29.7-13.3 29.7-29.7C448 383.8 368.2 304 269.7 304H178.3z" />
                      </svg>
                      <div className="flex w-full content-between">
                        <div className="text-lg font-medium text-white">
                          {getWriterId(rv.writer)}
                        </div>
                      </div>
                      <div
                        className="text-2xl font-bold text-center text-white mb-2"
                        style={{ whiteSpace: "nowrap" }}>
                        {Array.from({ length: 5 }, (_, index) => (
                          <StarIcon
                            key={index}
                            sx={{ color: index < rv.grade ? "#FFD700" : "gray", fontSize: 24 }}
                          />
                        ))}
                      </div>
                    </div>
                    <div className="text-lg font-medium text-white mb-4">
                      {rv.content}
                    </div>
                    <div className="text-lg font-medium text-white mb-2">
                      {rv.date}
                    </div>
