import { useState } from 'react';

export default function LikeDislikeFunc({ reviewId, love, hate }) {
  const [loveCount, setLoveCount] = useState(love);
  const [hateCount, setHateCount] = useState(hate);
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);

  const updateLove = (newLove) => {
    fetch(`http://192.168.56.1:8080/movie/love/${reviewId}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ love: newLove }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Network response was not ok');
        }
        return res.json();
      })
      .then((data) => {
        setLoveCount(data.love);
        setHateCount(data.hate);
      })
      .catch((err) => console.error(err));
  };

  const updateHate = (newHate) => {
    fetch(`http://192.168.56.1:8080/movie/hate/${reviewId}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ hate: newHate }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Network response was not ok');
        }
        return res.json();
      })
      .then((data) => {
        setLoveCount(data.love);
        setHateCount(data.hate);
      })
      .catch((err) => console.error(err));
  };

  const handleLike = () => {
    if (isLiked) {
      // 좋아요를 취소하는 경우
      updateLove(-1);
      setIsLiked(false);
      setLoveCount((prev) => Math.max(prev - 1, 0)); // 최소 0 유지
    } else {
      // 좋아요를 추가하는 경우
      updateLove(1);
      setIsLiked(true);
      setLoveCount((prev) => prev + 1);
      
      // 싫어요 상태 초기화
      if (isDisliked) {
        updateHate(-1); // 싫어요를 취소
        setIsDisliked(false);
        setHateCount((prev) => Math.max(prev - 1, 0));
      }
    }
  };

  const handleDislike = () => {
    if (isDisliked) {
      // 싫어요를 취소하는 경우
      updateHate(-1);
      setIsDisliked(false);
      setHateCount((prev) => Math.max(prev - 1, 0));
    } else {
      // 싫어요를 추가하는 경우
      updateHate(1);
      setIsDisliked(true);
      setHateCount((prev) => prev + 1);
      
      // 좋아요 상태 초기화
      if (isLiked) {
        updateLove(-1); // 좋아요를 취소
        setIsLiked(false);
        setLoveCount((prev) => Math.max(prev - 1, 0));
      }
    }
  };

  return (
    <div>
      <button onClick={handleLike}>
        좋아요 {loveCount}
      </button>
      <button onClick={handleDislike}>
        싫어요 {hateCount}
      </button>
    </div>
  );
}
