export default function LikeDislikeFunc({ reviewId, love, hate }) {
    const [like, setLike] = useState(love);
    const [dislike, setDislike] = useState(hate);
  //   const [isLiked, setIsLiked] = useState(false);
  //   const [isDisliked, setIsDisliked] = useState(false);   
    const [isLiked, setIsLiked] = useState(love > 0);
    const [isDisliked, setIsDisliked] = useState(hate > 0);
  
    const handleLike = () => {
        if (!isLiked && !isDisliked) {
            setLike((prev) => prev + 1);
            setIsLiked(true);
            // 서버에 좋아요 추가 요청
            fetchLoveUpdate(1);
        } else if (isLiked) {
            setLike((prev) => prev - 1);
            setIsLiked(false);
            // 서버에 좋아요 취소 요청
            fetchLoveUpdate(-1);
        } else if (isDisliked) {
            setLike((prev) => prev + 1);
            setIsLiked(true);
            setDislike((prev) => prev - 1);
            setIsDisliked(false);
            // 서버에 좋아요 추가 및 싫어요 취소 요청
            fetchLoveUpdate(1);
            fetchHateUpdate(-1);
        }
    };
    
    const handleDislike = () => {
        if (!isLiked && !isDisliked) {
            setDislike((prev) => prev + 1);
            setIsDisliked(true);
            // 서버에 싫어요 추가 요청
            fetchHateUpdate(1);
        } else if (isDisliked) {
            setDislike((prev) => prev - 1);
            setIsDisliked(false);
            // 서버에 싫어요 취소 요청
            fetchHateUpdate(-1);
        } else if (isLiked) {
            setDislike((prev) => prev + 1);
            setIsDisliked(true);
            setLike((prev) => prev - 1);
            setIsLiked(false);
            // 서버에 싫어요 추가 및 좋아요 취소 요청
            fetchHateUpdate(1);
            fetchLoveUpdate(-1);
        }
    };
    
    const fetchLoveUpdate = (action) => {
        fetch(`http://192.168.56.1:8080/movie/love/${reviewId}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                love: action,
            }),
        })
            .then(handleResponse)
            .catch((err) => console.error(err));
    };
    
    const fetchHateUpdate = (action) => {
        fetch(`http://192.168.56.1:8080/movie/hate/${reviewId}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                hate: action,
            }),
        })
            .then(handleResponse)
            .catch((err) => console.error(err));
    };
    
    const handleResponse = (res) => {
        if (!res.ok) {
            throw new Error('Network response was not ok');
        }
        return res.json().then((data) => {
            console.log('서버 응답:', data);
            setLike(data.love);
            setDislike(data.hate);
        });
    };
    
  
  
  
    return (
      <div>
        <button onClick={handleLike} className="text-white">
          좋아요 {like}
        </button>
  
        <button onClick={handleDislike} className="text-white">
          싫어요 {dislike}
        </button>
      </div>
    );
  }
