import React, { useEffect, useState } from 'react';

export default function LikeDislikeFunc({ reviewId, love, hate }) {
    const [like, setLike] = useState(love);
    const [dislike, setDislike] = useState(hate);
    const [isLiked, setIsLiked] = useState(false);
    const [isDisliked, setIsDisliked] = useState(false);
  
    useEffect(() => {
      if (isLiked) {
          fetch(`http://192.168.56.1:8080/movie/love/${reviewId}`, {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json',
              },
              body: JSON.stringify({
                  love: 1,
              }),
          })
              .then((res) => {
                  if (!res.ok) {
                      throw new Error('Network response was not ok');
                  }
                  return res.json();
              })
              .then((data) => {
                  console.log('서버 응답:', data);
                  setLike(data.love);
                  setDislike(data.hate);
              })
              .catch((err) => console.error(err));
      } else if (isDisliked) {
          fetch(`http://192.168.56.1:8080/movie/hate/${reviewId}`, {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json',
              },
              body: JSON.stringify({
                  hate: 1,
              }),
          })
              .then((res) => {
                  if (!res.ok) {
                      throw new Error('Network response was not ok');
                  }
                  return res.json();
              })
              .then((data) => {
                  console.log('서버 응답:', data);
                  setLike(data.love);
                  setDislike(data.hate);
              })
              .catch((err) => console.error(err));
      }
    }, [isLiked, isDisliked, reviewId]);
  
    const handleLike = () => {
      if (!isLiked && !isDisliked) {
          setLike((prev) => prev + 1);
          setIsLiked(true);
      } else if (isLiked) {
          setLike((prev) => prev - 1);
          setIsLiked(false);
      } else if (isDisliked) {
          setLike((prev) => prev + 1);
          setIsLiked(true);
          setDislike((prev) => prev - 1);
          setIsDisliked(false);
      }
    };
  
    const handleDislike = () => {
      if (!isLiked && !isDisliked) {
          setDislike((prev) => prev + 1);
          setIsDisliked(true);
      } else if (isDisliked) {
          setDislike((prev) => prev - 1);
          setIsDisliked(false);
      } else if (isLiked) {
          setDislike((prev) => prev + 1);
          setIsDisliked(true);
          setLike((prev) => prev - 1);
          setIsLiked(false);
      }
    };
  
    return (
      <div>
        <button onClick={handleLike} className="text-white">
          좋아요 {like}
        </button>
  
        <button onClick={handleDislike} className="text-white">
          싫어요 {dislike}
        </button>
      </div>
    );
  }
