import React, { useEffect, useState } from 'react'

export default function LikeDislikeFunc({reviewId, love, hate}) {

    // 좋아요 갯수 담을 변수
    const [like, setLike] = useState(2)
    // 싫어요 갯수 담을 변수
    const [dislike, setDislike] = useState(2)
    // 좋아요 상태 변수 : 처음에는 좋아요 되어있지 않음
    const [isLiked, setIsLiked] = useState(false)
    // 싫어요 상태 변수 : 처음에는 싫어요 되어있지 않음
    const [isDisliked, setIsDisliked] = useState(false)


    const handleLike = () => {
        // 1. 아무것도 눌려져있지 않다면 좋아요 1 올리기
        if(!isLiked && !isDisliked) {
            setLike(like + 1)
        } 
        // 2. 좋아요 버튼이 이미 클릭되어있다면 좋아요 1 내리기
        else if(isLiked) {
            setLike(like - 1)
        } 
        // 3. 싫어요 버튼이 이미 클릭 되어있다면 좋아요 1 올리고 싫어요 1 내리기
        else if(isDisliked) {
            setLike(like + 1)
            setDislike(dislike - 1)
        }
    }

    const handleDislike = () => {
        // 1. 아무것도 클릭되어있지 않다면 싫어요 1 올리기
        if(!isLiked && !isDisliked) {
            setDislike(dislike + 1)
        }
        // 2. 싫어요 버튼이 이미 클릭되어있다면 싫어요 1 내리기
        else if(isDisliked) {
            setDislike(dislike - 1)
        }
        // 3. 좋아요 버튼이 이미 클릭 되어있다면 싫어요 1 올리기 좋아요 1 내리기
        else if(isLiked) {
            setDislike(dislike + 1)
            setLike(like - 1)
        }
    }

  return (
   <div>
        <button onClick={handleLike}>
            좋아요 {like}
        </button>

        <button onClick={handleDislike}>
            싫어요 {dislike}
        </button>
   </div>
  )
}
