import React, { useState } from 'react';

function LikeDislikeFunc({reviewId}) {
    const [like, setLike] = useState(0);
    const [dislike, setDislike] = useState(0);
    const [isLiked, setIsLiked] = useState(false);
    const [isDisliked, setIsDisliked] = useState(false);

    const updateLike = (value) => {
       
        console.log("loveValue: ", value)

        fetch(`http://192.168.56.1:8080/movie/love/${reviewId}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                love: value
            })
        })
        .then((res) => {
            // ÏùëÎãµ ÏÉÅÌÉúÍ∞Ä ÏÑ±Í≥µÏ†ÅÏù¥Î©¥ JSONÏúºÎ°ú ÌååÏã±
            if (!res.ok) {
                throw new Error('Network response was not ok');
            }
            return res.json();
        })
        .then((data) => {
            console.log("ÏÑúÎ≤Ñ ÏùëÎãµ:", data); // ÏÑúÎ≤ÑÏùò ÏùëÎãµ ÎÇ¥Ïö© ÌôïÏù∏
            // ÌïÑÏöîÌïú Î°úÏßÅ Ï∂îÍ∞Ä
            setLike(data.love); // ÏóÖÎç∞Ïù¥Ìä∏Îêú Ï¢ãÏïÑÏöî ÏàòÎ°ú ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
        })
        .catch((err) => console.error(err));
    }

    const updateDislike = (value) => {
       
        console.log("hateValue: ", value)

        fetch(`http://192.168.56.1:8080/movie/hate/${reviewId}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                hate: value
            })
        })
        .then((res) => {
            // ÏùëÎãµ ÏÉÅÌÉúÍ∞Ä ÏÑ±Í≥µÏ†ÅÏù¥Î©¥ JSONÏúºÎ°ú ÌååÏã±
            if (!res.ok) {
                throw new Error('Network response was not ok');
            }
            return res.json();
        })
        .then((data) => {
            console.log("ÏÑúÎ≤Ñ ÏùëÎãµ:", data); // ÏÑúÎ≤ÑÏùò ÏùëÎãµ ÎÇ¥Ïö© ÌôïÏù∏
            // ÌïÑÏöîÌïú Î°úÏßÅ Ï∂îÍ∞Ä
            setDislike(data.hate); // ÏóÖÎç∞Ïù¥Ìä∏Îêú Ï¢ãÏïÑÏöî ÏàòÎ°ú ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
        })
        .catch((err) => console.error(err));
    }


    // Ï¢ãÏïÑÏöî Î≤ÑÌäº ÌÅ¥Î¶≠ Ï≤òÎ¶¨ Ìï®Ïàò
    const handleLike = () => {
        if (isLiked) {
            setLike(like - 1); // Ï¢ãÏïÑÏöî Ï∑®ÏÜå
            setIsLiked(false);
            updateLike(-1) // Ï¢ãÏïÑÏöî Ï∑®ÏÜå Ï†ÑÏÜ°
        } else {
            setLike(like + 1); // Ï¢ãÏïÑÏöî
            setIsLiked(true);
            updateLike(1) // Ï¢ãÏïÑÏöî Ï†ÑÏÜ°
            if (isDisliked) {
                setDislike(dislike - 1); // Ïã´Ïñ¥Ïöî Ï∑®ÏÜå
                setIsDisliked(false);
                updateDislike(-1) // Ïã´Ïñ¥Ïöî Ï∑®ÏÜå Ï†ÑÏÜ°
            }
        }
    };

    // Ïã´Ïñ¥Ïöî Î≤ÑÌäº ÌÅ¥Î¶≠ Ï≤òÎ¶¨ Ìï®Ïàò
    const handleDislike = () => {
        if (isDisliked) {
            setDislike(dislike - 1); // Ïã´Ïñ¥Ïöî Ï∑®ÏÜå
            setIsDisliked(false);
            updateDislike(-1) // Ïã´Ïñ¥Ïöî Ï∑®ÏÜå Ï†ÑÏÜ°
        } else {
            setDislike(dislike + 1); // Ïã´Ïñ¥Ïöî
            setIsDisliked(true);
            updateDislike(1) // Ïã´Ïñ¥Ïöî Ï†ÑÏÜ°
            if (isLiked) {
                setLike(like - 1); // Ï¢ãÏïÑÏöî Ï∑®ÏÜå
                setIsLiked(false);
                updateLike(-1) // Ï¢ãÏïÑÏöî Ï∑®ÏÜå Ï†ÑÏÜ°
            }
        }
    };

    return (
        <div>
            <button onClick={handleLike} className='text-white'>
                üëç Ï¢ãÏïÑÏöî {like}
            </button>
            <button onClick={handleDislike} className='text-white'>
                üëé Ïã´Ïñ¥Ïöî {dislike}
            </button>
        </div>
    );
}

export default LikeDislikeFunc;
