import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import FindMovieData from "./FindMovieData";
import StarIcon from "@mui/icons-material/Star";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import { red } from "@mui/material/colors";
import { Bounce, ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function MovieInfo_renew() {
  const param = useParams().index;
  const id = param;
  const movieInfo = FindMovieData(id);

  const [dbReview, setDbReview] = useState([]);
  const [content, setContent] = useState("");
  const [grade, setGrade] = useState(0);
  const [selectedReviewId, setSelectedReviewId] = useState(null);
  const [selectedDeleteId, setSelectedDeleteId] = useState(null);
  const [selectedContent, setSelectedContent] = useState("");
  const [selectedGrade, setSelectedGrade] = useState(0);
  const [sortBy, setSortBy] = useState("date"); 
  const [orderBy, setOrderBy] = useState("desc");
  const [sortedReviews, setSortedReviews] = useState([]);

  const getDbReview = (id, sortby, orderby) => {
    fetch(`http://192.168.56.1:8080/movie/review/${id}?ordertype=${sortby}&order=${orderby}`, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        setDbReview(data);
        setSortedReviews(data);
      })
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    getDbReview(id, sortBy, orderBy);
  }, [id, sortBy, orderBy]);

  const handleSortReviews = (newSortBy, newOrderBy) => {
    let sortedData = [...sortedReviews];

    if (newSortBy === "grade") {
      sortedData.sort((a, b) => newOrderBy === "asc" ? a.grade - b.grade : b.grade - a.grade);
    } else if (newSortBy === "date") {
      sortedData.sort((a, b) => newOrderBy === "asc" ? new Date(a.date) - new Date(b.date) : new Date(b.date) - new Date(a.date));
    }

    setDbReview(sortedData);
  };

  useEffect(() => {
    handleSortReviews(sortBy, orderBy);
  }, [sortBy, orderBy]);

  const handleContent = (e) => setContent(e.target.value);
  const handleGrade = (e) => setGrade(e.target.value);

  const toastSuccess = (message) => toast.success(message);
  const toastErr = (message) => toast.error(message);

  const handleInsert = () => {
    fetch(`http://192.168.56.1:8080/movie/review/${id}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
      body: JSON.stringify({ content, grade }),
    })
      .then((res) => {
        if (res.ok) {
          toastSuccess("리뷰 등록 성공");
          window.location.reload();
        } else {
          throw new Error("리뷰 등록 실패");
        }
      })
      .catch((err) => {
        console.error(err);
        toastErr('리뷰 등록 권한 에러');
      });
  };

  const handleUpdate = (reviewId) => {
    fetch(`http://192.168.56.1:8080/movie/review/${reviewId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
      body: JSON.stringify({ content, grade }),
    })
      .then((res) => {
        if (res.ok) {
          toastSuccess("리뷰 수정 성공");
          window.location.reload();
        } else {
          throw new Error("리뷰 수정 실패");
        }
      })
      .catch((err) => {
        console.error(err);
        toastErr('리뷰 수정 권한 에러');
      });
  };

  const handleDelete = (reviewId) => {
    fetch(`http://192.168.56.1:8080/movie/review/${reviewId}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
    })
      .then((res) => {
        if (res.ok) {
          toastSuccess("리뷰 삭제 성공");
          window.location.reload();
        } else {
          throw new Error("리뷰 삭제 실패");
        }
      })
      .catch((err) => {
        console.error(err);
        toastErr('리뷰 삭제 권한 에러');
      });
  };

  const [openModify, setOpenModify] = useState(false);
  const [openDelete, setOpenDelete] = useState(false);
  const [openInsert, setOpenInsert] = useState(false);

  const handleClickOpenUpdate = (e, reviewId, content, grade) => {
    setSelectedReviewId(reviewId);
    setSelectedContent(content);
    setSelectedGrade(grade);
    setOpenModify(true);
  };

  const handleClickOpenDelete = (e, reviewId) => {
    setSelectedDeleteId(reviewId);
    setOpenDelete(true);
  };

  const handleClickOpenInsert = (e) => setOpenInsert(true);

  const handleClose = () => {
    setSelectedReviewId(null);
    setSelectedDeleteId(null);
    setOpenModify(false);
    setOpenDelete(false);
    setOpenInsert(false);
  };

  return (
    <div className="bg-black h-full w-full">
      {movieInfo && (
        <div>
          <div className="p-10 flex">
            <img
              src={`https://image.tmdb.org/t/p/w500/${movieInfo.backdrop_path}`}
              className="w-full mb-10"
            />
            <div className="ml-5">
              <div className="text-3xl font-extrabold mb-3 text-white">{movieInfo.title}</div>
              <div className="text-2xl font-extrabold mb-3 text-white">{movieInfo.original_title}</div>
              <div className="text-xl font-extrabold mb-3 text-white">{movieInfo.tagline}</div>
              <div className="text-lg font-medium text-white">개봉일: {movieInfo.release_date}</div>
              <div className="text-lg font-medium text-white">상영시간: {movieInfo.runtime}분</div>
              <div className="text-lg font-medium text-white">부적절 등급: {movieInfo.adult ? "청소년 관람 불가" : "청소년 관람 가능"}</div>
              <div className="text-lg font-medium text-white">장르: {movieInfo.genres && movieInfo.genres.map(genre => genre.name).join(', ')}</div>
              <div className="flex justify-start items-start text-lg font-medium text-white">
                <span className="inline-block w-auto lg:min-w-20">시놉시스:</span>
                <span className="inline-block text-justify">{movieInfo.overview}</span>
              </div>
              <div className="flex justify-end">
                <Button variant="contained" color="primary" onClick={handleClickOpenInsert}>
                  리뷰 작성
                </Button>
              </div>
            </div>
          </div>

          {dbReview && dbReview.length > 0 && (
            <div>
              <div className="p-5">
                <label className="mr-2 text-white">정렬 기준:</label>
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="mr-4"
                >
                  <option value="date">날짜</option>
                  <option value="grade">별점</option>
                </select>
                <label className="mr-2 text-white">정렬 순서:</label>
                <select
                  value={orderBy}
                  onChange={(e) => setOrderBy(e.target.value)}
                >
                  <option value="desc">내림차순</option>
                  <option value="asc">오름차순</option>
                </select>
              </div>

              {dbReview.map((review) => (
                <div key={review.id} className="p-5 border-t border-white">
                  <div className="text-xl font-bold text-white">{review.writer}</div>
                  <div className="flex items-center mb-3">
                    <StarIcon color="warning" />
                    <span className="text-white ml-2">{review.grade}</span>
                  </div>
                  <div className="text-white mb-3">{review.content}</div>
                  <div className="flex justify-end">
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={(e) => handleClickOpenUpdate(e, review.id, review.content, review.grade)}
                      className="mr-2"
                    >
                      수정
                    </Button>
                    <Button
                      variant="contained"
                      color="error"
                      onClick={(e) => handleClickOpenDelete(e, review.id)}
                    >
                      삭제
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* 리뷰 작성 모달 */}
          <Dialog open={openInsert} onClose={handleClose}>
            <DialogTitle>리뷰 작성</DialogTitle>
            <DialogContent>
              <DialogContentText>
                리뷰를 작성해주세요.
              </DialogContentText>
              <TextField
                autoFocus
                margin="dense"
                id="content"
                label="리뷰 내용"
                type="text"
                fullWidth
                variant="standard"
                value={content}
                onChange={handleContent}
              />
              <TextField
                margin="dense"
                id="grade"
                label="별점 (1-10)"
                type="number"
                fullWidth
                variant="standard"
                value={grade}
                onChange={handleGrade}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose}>취소</Button>
              <Button onClick={handleInsert}>등록</Button>
            </DialogActions>
          </Dialog>

          {/* 리뷰 수정 모달 */}
          <Dialog open={openModify} onClose={handleClose}>
            <DialogTitle>리뷰 수정</DialogTitle>
            <DialogContent>
              <DialogContentText>
                리뷰를 수정해주세요.
              </DialogContentText>
              <TextField
                autoFocus
                margin="dense"
                id="content"
                label="리뷰 내용"
                type="text"
                fullWidth
                variant="standard"
                value={selectedContent}
                onChange={(e) => setSelectedContent(e.target.value)}
              />
              <TextField
                margin="dense"
                id="grade"
                label="별점 (1-10)"
                type="number"
                fullWidth
                variant="standard"
                value={selectedGrade}
                onChange={(e) => setSelectedGrade(e.target.value)}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose}>취소</Button>
              <Button onClick={() => handleUpdate(selectedReviewId)}>수정</Button>
            </DialogActions>
          </Dialog>

          {/* 리뷰 삭제 모달 */}
          <Dialog open={openDelete} onClose={handleClose}>
            <DialogTitle>리뷰 삭제</DialogTitle>
            <DialogContent>
              <DialogContentText>
                이 리뷰를 삭제하시겠습니까?
              </DialogContentText>
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose}>취소</Button>
              <Button onClick={() => handleDelete(selectedDeleteId)}>삭제</Button>
            </DialogActions>
          </Dialog>

          <ToastContainer transition={Bounce} autoClose={2000} />
        </div>
      )}
    </div>
  );
}
