import React, { useState } from "react";

const MemberUpdatePage = () => {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  // 회원 정보를 업데이트하는 함수
  const handleUpdate = async () => {
    if(password !== confirmPassword){
        return alert('Confirmed password is wrong')
    }

    try {
      const response = await fetch("/member/update", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ password }), // JSON 형태로 데이터 전송
      });
      const data = await response.json();
      console.log(data); // 성공 메시지 처리
    } catch (error) {
      console.error("회원 정보 업데이트 에러:", error);
    }
  };

  return (
    <div className="bg-black">
    <div className="flex-col justify-start items-center">
      <div className="text-2xl font-extrabold text-center text-red-400">회원 정보 수정</div>
      {/* <div>
        <label>이름:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div> */}
      <div className="text-center">
        <label>Password: </label>
        <input
          type="text"
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <div className="text-center">
        <label>Confirm Password: </label>
        <input
          type="text"
          onChange={(e) => setConfirmPassword(e.target.value)}
        />
      </div>
      <button onClick={handleUpdate}>수정</button>
    </div>
    </div>
  );
};

export default MemberUpdatePage;
