import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import FindMovieData from "./FindMovieData";
import StarIcon from "@mui/icons-material/Star";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import { red } from "@mui/material/colors";
import { Bounce, ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function MovieInfo_copy() {

  const param = useParams().index;
  const id = param;
  const movieInfo = FindMovieData(id);

  const [dbReview, setDbReview] = useState([]);
  const [content, setContent] = useState("");
  const [grade, setGrade] = useState("");
  const [selectedReviewId, setSelectedReviewId] = useState(null);
  const [selectedDeleteId, setSelectedDeleteId] = useState(null);
  const [selectedContent, setSelectedContent] = useState("");
  const [selectedGrade, setselectedGrade] = useState("");
  const [sortBy, setSortBy] = useState("date"); // 정렬 기준 (date 또는 grade)
  const [orderBy, setOrderBy] = useState("desc"); // 정렬 방식 (asc 또는 desc)

  // 리뷰 DB에서 리뷰 정보 받아오기
  const getDbReview = (id, sortBy, orderBy) => {
    fetch(`http://192.168.56.1:8080/movie/review/${id}?sortby=${sortBy}&orderby=${orderBy}`, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => setDbReview(data))
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    getDbReview(id, sortBy, orderBy); // sortBy와 orderBy 값을 전달
  }, [id, sortBy, orderBy]);

  // 작성자 이메일을 받아와서 이름 분리
  const getWriterId = (string) => string.substring(0, string.indexOf("@"));

  // 리뷰 내용 받아오기
  const handleContent = (e) => setContent(e.target.value);

  // 리뷰 별점 받아오기
  const handleGrade = (e) => setGrade(e.target.value);

  // toast 설정
  const toastSuccess = (message) => toast.success(message);
  const toastErr = (message) => toast.error(message);

  // 리뷰 등록 fetch
  const handleInsert = () => {
    fetch(`http://192.168.56.1:8080/movie/review/${id}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
      body: JSON.stringify({ content, grade }),
    })
      .then((res) => {
        if (res.ok) {
          toastSuccess("리뷰 등록 성공");
          window.location.reload();
        } else {
          throw new Error("리뷰 등록 실패");
        }
      })
      .catch((err) => {
        console.error(err);
        toastErr("리뷰 등록 권한 에러");
      });
  };

  // 리뷰 수정 fetch
  const handleUpdate = (reviewId) => {
    fetch(`http://192.168.56.1:8080/movie/review/${reviewId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
      body: JSON.stringify({ content, grade }),
    })
      .then((res) => {
        if (res.ok) {
          toastSuccess("리뷰 수정 성공");
          window.location.reload();
        } else {
          throw new Error("리뷰 수정 실패");
        }
      })
      .catch((err) => {
        console.error(err);
        toastErr("리뷰 수정 권한 에러");
      });
  };

  // 리뷰 삭제 fetch
  const handleDelete = (reviewId) => {
    fetch(`http://192.168.56.1:8080/movie/review/${reviewId}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.getItem("loginToken"),
      },
    })
      .then((res) => {
        if (!res.ok) throw new Error("Error");
        toastSuccess("리뷰 삭제 성공");
        window.location.reload();
      })
      .catch((err) => {
        console.error(err);
        toastErr("리뷰 삭제 권한 에러");
      });
  };

  const [openModify, setOpenModify] = React.useState(false);
  const [openDelete, setOpenDelete] = React.useState(false);
  const [openInsert, setOpenInsert] = React.useState(false);

  const handleClickOpenUpdate = (e, reviewId, content, grade) => {
    setSelectedReviewId(reviewId);
    setSelectedContent(content);
    setselectedGrade(grade);
    setOpenModify(true);
  };

  const handleClickOpenDelete = (e, reviewId) => {
    setSelectedDeleteId(reviewId);
    setOpenDelete(true);
  };

  const handleClickOpenInsert = (e) => setOpenInsert(true);

  const handleClose = () => {
    setSelectedReviewId(null);
    setSelectedDeleteId(null);
    setOpenModify(false);
    setOpenDelete(false);
    setOpenInsert(false);
  };

  return (
    <div className="bg-black h-full w-full">
      {movieInfo && (
        <div>
          <div className="p-10 flex">
            <img
              src={`https://image.tmdb.org/t/p/w500/${movieInfo.backdrop_path}`}
              className="w-full mb-10"
            />
            <div className="ml-5">
              <div className="text-3xl font-extrabold mb-3 text-white">
                {movieInfo.title}
              </div>
              <div className="text-2xl font-extrabold mb-3 text-white">
                {movieInfo.original_title}
              </div>
              <div className="text-xl font-extrabold mb-3 text-white">
                {movieInfo.tagline}
              </div>
              <div className="text-lg font-medium text-white">
                개봉일: {movieInfo.release_date}
              </div>
              <div className="text-lg font-medium text-white">
                상영시간: {movieInfo.runtime}분
              </div>
              <div className="text-lg font-medium text-white">
                부적절 등급: {movieInfo.adult ? "청소년 관람 불가" : "청소년 관람 가능"}
              </div>
              <div className="text-lg font-medium text-white">
                장르: {movieInfo.genres && movieInfo.genres.map(genre => genre.name).join(', ')}
              </div>
              <div className="flex justify-start items-start text-lg font-medium text-white">
                <span className="inline-block w-auto lg:min-w-20">시놉시스:</span>
                <div className="ml-1 w-full">{movieInfo.overview}</div>
              </div>
              <div className="text-lg font-medium text-white">
                관람객: {movieInfo.popularity}명
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="flex justify-end items-center text-white mb-3 mr-12">
        <React.Fragment>
          <Button variant="outlined" onClick={handleClickOpenInsert}
            style={{
              backgroundColor: red[400],
              fontWeight: "bold",
              color: "white",
              borderColor: red[400],
              marginRight: "5px",
            }}>
            등록
          </Button>
          <Dialog open={openInsert} onClose={handleClose}>
            <DialogTitle>등록</DialogTitle>
            <DialogContent>
              <DialogContentText>
                등록할 내용을 작성해주세요
              </DialogContentText>
              <TextField
                disabled
                id="email"
                label="Email"
                defaultValue={localStorage.getItem("userId")}
                variant="standard"
                fullWidth
              />
              <TextField
                onChange={handleContent}
                autoFocus
                required
                margin="dense"
                id="content"
                name="content"
                label="Content"
                type="text"
                fullWidth
                variant="standard"
              />
              <TextField
                onChange={handleGrade}
                autoFocus
                required
                margin="dense"
                id="grade"
                name="grade"
                label="Grade"
                type="text"
                fullWidth
                variant="standard"
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose}>취소</Button>
              <Button
                onClick={() => {
                  handleInsert();
                  handleClose();
                }}
              >
                등록
              </Button>
            </DialogActions>
          </Dialog>
        </React.Fragment>
      </div>

      <div className="flex justify-end items-center text-white mb-3 mr-12">
        <div className="flex space-x-4">
          <select value={sortBy} onChange={(e) => setSortBy(e.target.value)} className="bg-gray-700 text-white rounded-md px-4 py-2">
            <option value="date">날짜</option>
            <option value="grade">평점</option>
          </select>
          <select value={orderBy} onChange={(e) => setOrderBy(e.target.value)} className="bg-gray-700 text-white rounded-md px-4 py-2">
            <option value="desc">내림차순</option>
            <option value="asc">오름차순</option>
          </select>
        </div>
      </div>

      <div className="flex flex-col items-center">
        {dbReview.map((review) => (
          <div key={review.id} className="bg-gray-800 p-4 mb-2 rounded-lg w-full max-w-lg text-white">
            <div className="flex justify-between items-center mb-2">
              <div className="text-lg font-bold">{getWriterId(review.email)}</div>
              <div className="text-lg">
                {review.grade} <StarIcon style={{ color: "gold" }} />
              </div>
            </div>
            <div className="text-md mb-2">{review.content}</div>
            <div className="text-sm text-gray-400">{new Date(review.date).toLocaleDateString()}</div>
            <div className="flex justify-end space-x-2 mt-2">
              <Button
                onClick={(e) => handleClickOpenUpdate(e, review.id, review.content, review.grade)}
                style={{
                  backgroundColor: red[400],
                  color: "white",
                  borderColor: red[400],
                }}
              >
                수정
              </Button>
              <Button
                onClick={(e) => handleClickOpenDelete(e, review.id)}
                style={{
                  backgroundColor: red[400],
                  color: "white",
                  borderColor: red[400],
                }}
              >
                삭제
              </Button>
            </div>
          </div>
        ))}
      </div>

      <Dialog open={openModify} onClose={handleClose}>
        <DialogTitle>리뷰 수정</DialogTitle>
        <DialogContent>
          <DialogContentText>
            수정할 내용을 작성해주세요
          </DialogContentText>
          <TextField
            disabled
            id="email"
            label="Email"
            defaultValue={localStorage.getItem("userId")}
            variant="standard"
            fullWidth
          />
          <TextField
            onChange={(e) => setSelectedContent(e.target.value)}
            autoFocus
            margin="dense"
            id="content"
            label="Content"
            type="text"
            value={selectedContent}
            fullWidth
            variant="standard"
          />
          <TextField
            onChange={(e) => setselectedGrade(e.target.value)}
            autoFocus
            margin="dense"
            id="grade"
            label="Grade"
            type="text"
            value={selectedGrade}
            fullWidth
            variant="standard"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>취소</Button>
          <Button
            onClick={() => {
              handleUpdate(selectedReviewId);
              handleClose();
            }}
          >
            수정
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog open={openDelete} onClose={handleClose}>
        <DialogTitle>리뷰 삭제</DialogTitle>
        <DialogContent>
          <DialogContentText>
            정말로 이 리뷰를 삭제하시겠습니까?
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>취소</Button>
          <Button
            onClick={() => {
              handleDelete(selectedDeleteId);
              handleClose();
            }}
          >
            삭제
          </Button>
        </DialogActions>
      </Dialog>

      <ToastContainer
        position="bottom-right"
        autoClose={5000}
        transition={Bounce}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
    </div>
  );
}
